mapper.delete:
delete from shopping_cart
where user_id=#{userId}

	and product_id in
	
		#{item}

mapper.update:
update shopping_cart
set checked=#{checked},
update_time= now()
where
user_id=#{userId}

	and product_id=#{productId}

mapper.select:
select IFNULL(sum(product_num),0) from shopping_cart
where
user_id=#{userId}
